/*-- Body Reset --*/

body {
    overflow-x: hidden;
    font-family: 'Montserrat', sans-serif;
    color: #616971;
}


/*============= TOP BAR HEADER =============*/

.top-bar {
    background: #e9ecef;
    height: 2.8rem;
    padding: .5rem 0;
}

.top-bar a {
    color: #2c3531;
    text-decoration: none;
    font-size: 1.3rem;
    font-weight: 500;
}

.area-code {
    color: #116466;
}


/*============= NAVIGATION =============*/

.navbar-brand img {
    height: 3rem;
}

.navbar {
    font-size: 1rem;
    font-weight: 600;
    letter-spacing: .1rem;
    box-shadow: 0 0.5rem 0.5rem rgba(0, 0, 0, 0.1);
    z-index: 1;
    background: #116466!important;
}

.navbar .fa-hamburger {
    color: #FFCB9A;
}

.nav-item {
    padding: .5rem;
}

.nav-link {
    color: #e9ecef!important;
}

.nav-link.active,
.nav-link:hover {
    color: #FFCB9A!important;
}


/*--Hamburger icon in navbar--*/

.navbar-toggler-icon {
    height: 1em;
    width: 1em;
}


/*============= CAROUSEL =============*/

#carousel {
    box-shadow: 0 1rem 1rem rgba(0, 0, 0, 0.1);
}

.carousel-inner {
    max-height: 40rem!important;
}


/*---Change Carousel Transition Speed --*/

.carousel-item-next,
.carousel-item-prev,
.carousel-item.active {
    transition: transform 1.2s ease;
}


/*-- Carousel Content --*/

.carousel-caption {
    position: absolute;
    top: 54%;
    /*--centeres the element in the middle with the transform translateY--*/
    transform: translateY(-50%);
    text-transform: uppercase;
}

.bg-custom {
    background-color: rgba(0, 0, 0, 40%);
    color: white;
    border-radius: 1rem;
}

.carousel-caption h1 {
    font-size: 3.8rem;
    text-shadow: 0.1rem 0.1rem 0.3rem rgba(0, 0, 0, 1);
}

.border-primary {
    border-top: 0.2rem solid #116466!important;
}

.carousel-caption h3 {
    font-size: 2.2rem;
    text-shadow: 0.1rem 0.1rem 0.2rem rgba(0, 0, 0, 1);
}


/*--This effects all btn-lg buttons on the page--*/

.btn-lg {
    border-radius: 0;
    text-transform: uppercase;
    font-size: 1rem;
    border-width: medium;
}

.btn-danger {
    background-color: #d9b08c;
    border: .1rem solid #d9b08c;
}

.btn-primary {
    background-color: #116466;
    border: .05rem solid #116466;
}

.btn-primary:hover {
    background-color: #1c7274;
    border: .05rem solid #1c7274;
}

.btn-primary:focus {
    background-color: #1c7274!important;
    border: .05rem solid #1c7274!important;
}

.btn-danger:hover {
    background-color: #ffcb94;
    border: .05rem solid #ffcb94;
}

.btn-danger:focus {
    background-color: #ffcb94!important;
    border: .05rem solid #ffcb94!important;
}


/*===== FIXED BACKGROUND IMG =====*/

.fixed {
    position: fixed;
    background-image: url('/ddd3e5331f63c8404283129388b6cc0a.png');
}

.fixed-background {
    padding: 10rem 0;
    overflow: hidden;
    /*-- Correction for iOS --*/
}


/*-- Emoji Navbar --*/

.emoji {
    background-image: linear-gradient(to right, #4981b3, #ac2615);
    min-height: 0!important;
    padding: 1.4rem 1!important;
}

.emoji .svg-inline--fa {
    margin: 0 auto;
    color: white;
}

.emoji .fa-shoe-prints {
    color: #FFCB9A;
}


/*============= FOOTER =============*/

footer {
    background-color: #383838;
    padding: 1rem 2rem 3rem;
}

footer img {
    height: 3.5rem;
    margin: 1.5rem auto;
}

footer p,
footer a,
.socket a {
    color: white;
    text-decoration: none;
}

ul.social {
    list-style-type: none;
    display: flex;
    max-width: 16rem;
    margin: 0 auto;
    padding: 0;
}

ul.social li {
    margin: 0 auto;
}

footer .svg-inline--fa:hover {
    color: #68a0d2;
}


/*============= SOCKET =============*/

.socket {
    background-color: #222222;
    border-top: .1rem solid white;
}


/*============= MEDIA QUERIES =============*/


/* Devices over 1650px (xl) */

@media (min-width: 1649.98px) {
    .carousel-inner {
        max-height: 50rem!important;
        min-width: 100%;
    }
    .carousel-item img {
        position: relative;
        top: -150px;
    }
}


/* Devices under 1199px (xl) */

@media (max-width: 1199.98px) {
    .carousel-caption h1 {
        font-size: 2.8rem;
    }
    .carousel-caption h3 {
        font-size: 2.2rem;
    }
}


/* Devices under 992px (md) */

@media (max-width: 991.98px) {
    .carousel-caption h1 {
        font-size: 2.5rem;
    }
    .carousel-caption h3 {
        font-size: 1.8rem;
    }
    .btn-lg {
        font-size: .95rem;
    }
}


